import { createSlice, PayloadAction } from '@reduxjs/toolkit';
import { AppThunk } from './store';

interface Task {
  id: string;
  title: string;
  status: string;
}

interface Board {
  id: string;
  title: string;
  tasks: Task[];
}
interface TasksState {
  tasks: Task[];
  boards: Board[];
}

const initialState: TasksState = {
  tasks: [],
  boards: [{ id: '1', title: 'To Do', tasks: [] },
  { id: '2', title: 'In Progress', tasks: [] },
  { id: '3', title: 'Done', tasks: [] },],
};

const tasksSlice = createSlice({
  name: 'tasks',
  initialState,
  reducers: {
    setTasks: (state, action: PayloadAction<Task[]>) => {
      state.tasks = action.payload;
    },
    addTask: (state, action: PayloadAction<Task>) => {
      state.tasks.push(action.payload);
      localStorage.setItem('tasks', JSON.stringify(state.tasks));
    },
    updateTaskInState: (state, action: PayloadAction<Task[]>) => {
      state.tasks = action.payload;
      localStorage.setItem('tasks', JSON.stringify(state.tasks));
    },
    deleteTask: (state, action: PayloadAction<string>) => {
      state.tasks = state.tasks.filter(task => task.id !== action.payload);
      localStorage.setItem('tasks', JSON.stringify(state.tasks));
    },
    addBoard: (state, action: PayloadAction<string>) => {
      const newBoard: Board = {
          id: (state.boards.length + 1).toString(),
          title: action.payload,
          tasks: [],
      };
      state.boards.push(newBoard);
  }
  },
});

export const { setTasks, addTask, updateTaskInState, deleteTask, addBoard } = tasksSlice.actions;

export const fetchTasks = (): AppThunk => dispatch => {
  const tasks = localStorage.getItem('tasks');
  if (tasks) {
    dispatch(setTasks(JSON.parse(tasks)));
  }
};

export default tasksSlice.reducer;
