import { createSlice } from '@reduxjs/toolkit';
const initialState = {
  boards: [],
  selectedBoardId: null
};
const boardsSlice = createSlice({
  name: 'boards',
  initialState,
  reducers: {
    addBoard: (state, action) => {
      state.boards.push({
        id: action.payload.id,
        name: action.payload.name,
        tasks: []
      });
      localStorage.setItem('boards', JSON.stringify(state.boards));
    },
    selectBoard: (state, action) => {
      state.selectedBoardId = action.payload;
    },
    addTaskToBoard: (state, action) => {
      const board = state.boards.find(board => board.id === action.payload.boardId);
      if (board) {
        board.tasks.push(action.payload.task);
        localStorage.setItem('boards', JSON.stringify(state.boards));
      }
    },
    updateTaskInBoard: (state, action) => {
      const board = state.boards.find(board => board.id === action.payload.boardId);
      if (board) {
        board.tasks = action.payload.tasks;
        localStorage.setItem('boards', JSON.stringify(state.boards));
      }
    },
    deleteTaskFromBoard: (state, action) => {
      const board = state.boards.find(board => board.id === action.payload.boardId);
      if (board) {
        board.tasks = board.tasks.filter(task => task.id !== action.payload.taskId);
        localStorage.setItem('boards', JSON.stringify(state.boards));
      }
    }
  }
});
export const {
  addBoard,
  selectBoard,
  addTaskToBoard,
  updateTaskInBoard,
  deleteTaskFromBoard
} = boardsSlice.actions;
