import { createSlice, PayloadAction } from '@reduxjs/toolkit';
import { AppThunk } from './store';

interface Task {
    id: string;
    title: string;
    status: string;
}

interface Board {
    id: string;
    name: string;
    tasks: Task[];
}

interface BoardsState {
    boards: Board[];
    selectedBoardId: string | null;
}

const initialState: BoardsState = {
    boards: [],
    selectedBoardId: null,
};

const boardsSlice = createSlice({
    name: 'boards',
    initialState,
    reducers: {
        addBoard: (state, action: PayloadAction<{ id: string; name: string }>) => {
            state.boards.push({ id: action.payload.id, name: action.payload.name, tasks: [] });
            localStorage.setItem('boards', JSON.stringify(state.boards));
        },
        selectBoard: (state, action: PayloadAction<string>) => {
            state.selectedBoardId = action.payload;
        },
        addTaskToBoard: (state, action: PayloadAction<{ boardId: string; task: Task }>) => {
            const board = state.boards.find(board => board.id === action.payload.boardId);
            if (board) {
                board.tasks.push(action.payload.task);
                localStorage.setItem('boards', JSON.stringify(state.boards));
            }
        },
        updateTaskInBoard: (state, action: PayloadAction<{ boardId: string; tasks: Task[] }>) => {
            const board = state.boards.find(board => board.id === action.payload.boardId);
            if (board) {
                board.tasks = action.payload.tasks;
                localStorage.setItem('boards', JSON.stringify(state.boards));
            }
        },
        deleteTaskFromBoard: (state, action: PayloadAction<{ boardId: string; taskId: string }>) => {
            const board = state.boards.find(board => board.id === action.payload.boardId);
            if (board) {
                board.tasks = board.tasks.filter(task => task.id !== action.payload.taskId);
                localStorage.setItem('boards', JSON.stringify(state.boards));
            }
        },
    },
});

export const { addBoard, selectBoard, addTaskToBoard, updateTaskInBoard, deleteTaskFromBoard } = boardsSlice.actions;

export const fetchBoards = (): AppThunk => dispatch => {
    const boards = localStorage.getItem('boards');
    if (boards) {
        dispatch(setBoards(JSON.parse(boards)));
    }
};

export default boardsSlice.reducer;


