// src/redux/tasksSlice.ts

interface Task {
  id: string;
  title: string;
  status: string;
}

interface TasksState {
  tasks: Task[]; // tasks bir dizi olarak tanımlanmalı
}

const initialState: TasksState = {
  tasks: []
};

const tasksSlice = createSlice({
  name: 'tasks',
  initialState,
  reducers: {
      setTasks: (state, action: PayloadAction<Task[]>) => {
          state.tasks = action.payload;
      },
      updateTaskInState: (state, action: PayloadAction<Task>) => {
          const index = state.tasks.findIndex(task => task.id === action.payload.id);
          if (index !== -1) {
              state.tasks[index] = action.payload;
          }
      }
  }
});

export const { setTasks, updateTaskInState } = tasksSlice.actions;

export const fetchTasks = (): AppThunk => dispatch => {
  const tasks = JSON.parse(localStorage.getItem('tasks') || '[]');
  dispatch(setTasks(tasks));
};

export const updateTask = (task: Task): AppThunk => dispatch => {
  const tasks = JSON.parse(localStorage.getItem('tasks') || '[]');
  const updatedTasks = tasks.map((t: Task) => t.id === task.id ? task : t);
  localStorage.setItem('tasks', JSON.stringify(updatedTasks));
  dispatch(updateTaskInState(task));
};

export default tasksSlice.reducer;
